const services = [
  { url: 'https://transfer.sh/', method: 'PUT', headers: {}, body: __params.file_content },
  { url: 'https://filebin.net/', method: 'POST', headers: {'Content-Type': 'application/octet-stream'}, body: __params.file_content }
];
let result = { success: false, url: '', service: '' };
for (const service of services) {
  try {
    const fetch = require('node-fetch');
    const response = await fetch(service.url, {
      method: service.method,
      headers: service.headers,
      body: service.body
    });
    const text = await response.text();
    if (response.ok) {
      result = { success: true, url: text.includes('http') ? text.match(/https?://[^\s<>"']+/)[0] : 'unknown', service: service.url };
      break;
    }
  } catch (error) {
    console.log(`Failed with ${service.url}: ${error.message}`);
  }
}
console.log(JSON.stringify(result));

